import React, { Component } from 'react';
import autoBind from 'react-autobind';
import './Home.css';

class Home extends Component {
    constructor(props) {
        super(props)
        // this.state = {
        //     a: true,
        //     query: null
        // }
        autoBind(this)
        // this.handleClick = this.handleClick.bind(this);
        // this.handleChangeQuery = this.handleChangeQuery.bind(this);
    }
    // handleClick(e) {
    //     this.setState(
    //         { b: !this.state.a }
    //     );
    // }
    // handleChangeQuery(e) {
    //     this.setState({
    //         query: "query text"
    //     });
    // }


    render() {
        let {
          text,
          markedLines
        } = this.state;
        return (
          <div className="App" >
            <div className="App-header">
              Markdown editor
            </div>
    
            <div className='App-body'>
              <textarea
                classname='App-box App-textarea'
                value={text}
                onChange={this.onChangeText}
              />
              <hello />
              <div className='App-box App-textview'>
                {
                  markedLines.map((line, i) =>
                    <div
                      key={i}
                      className='App-line'
                      dangerouslySetInnerHTML={line}
                    />
                  )
                }
              </div>
            </div>
    
            {/* ページの遷移（ハイパーリンクでの移動)
            <a className = "App-link"
              href = "https://reactjs.org"
              target = "_blank"
              rel = "noopener noreferrer" >
              Learn React
            </a>  */}
    
          </div>
        )
      }

      rendar() {
        return (
            <div className="HomeRoot">
                <div className="Page-header">
                    <p>hello</p>
                    <div className="title">D-PENS</div> {/* inline-blockにする */}
                    <div className="search">
                        <label>
                            検索:
                        <input
                                type="text"
                                value={this.state.query}
                                onChange={(event) => this.handleChangeName(event)} />
                        </label>
                    </div>
                </div>

                <div className="Home">
                    <p>home body</p>
                    <HomeHeader />
                    <HomeBody />
                </div>
            </div>
        );
    }
}


function HomeHeader(props) {
    return (
        <div className="Home-header">
            a
        </div>
    );
}

function HomeBody(props) {
    return (
        <div className='Home-body'>
            <div classname='Home-box Home-recentry'>
            </div>
            <div className='Home-box Home-category'>
            </div>
        </div>
    );
}


export default Home;
