// https://hfuji.hatenablog.jp/entry/2017/07/01/002723/
import React, { Component } from 'react';
import autoBind from 'react-autobind';
import marked from 'marked';
// import logo from './logo.svg';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props)
    autoBind(this)
    this.state = {
      text: '',
      markedLines: []
    }
  }

  render() {
    var name = "takeru";
    var age = 21;

    let {
      text,
      markedLines
    } = this.state;
    return (
      <div className="App" >

        <div className="App-header">
          Markdown editor
        </div>

        <hello name={name} age={age} />

        <div className='App-body'>
          <textarea
            classname='App-box App-textarea'
            value={text}
            onChange={this.onChangeText}
          />
          <div className='App-box App-textview'>
            {
              markedLines.map((line, i) =>
                <div
                  key={i}
                  className='App-line'
                  dangerouslySetInnerHTML={line}
                />
              )
            }
          </div>
        </div>

        {/* ページの遷移（ハイパーリンクでの移動)
        <a className = "App-link"
          href = "https://reactjs.org"
          target = "_blank"
          rel = "noopener noreferrer" >
          Learn React
        </a>  */}

      </div>
    )
  }

  hello(props) {
    return (
      <p>
        Hello ears old
      </p>
    );
  }


  onChangeText(e) {
    let text = e.target.value
    let markedLines = text
      .split('\n')
      .map((line) => ({ __html: marked(line) }))
    this.setState({
      text,
      markedLines
    })
  }
}

export default App;
